UPDATE private.waypoints_i18n_es SET description='Para llegar al agujero de Malhivern, se cruzan tres esquinas muy rectas donde es posible disparar alas a nuestro paso si el equilibrio del manto es débil.. El segundo esgón puede evitarse desviándose un poco hacia la izquierda para buscar terreno más suave (ver mapa)' WHERE id='295';
UPDATE private.waypoints_i18n_es SET description='El descenso de la pala oeste del pico de las Fuentes es un buen esquí. Es 150 metros de desnivel entre 30 y 40 grados de pendiente. Hay que elegir un día de buenas condiciones para hacer este descenso' WHERE id='298';
