UPDATE private.waypoints_i18n_es SET description='Flanqueo de Costa Gran. Durante unos 350 metros estarás expuesto a zonas de llegada de las alas naturales habituales.. El bosque debajo de ti puede actuar como una trampa de terreno en caso de accidente.. Espiad el grupo' WHERE id='305';
UPDATE private.waypoints_i18n_es SET description='Resaltado de la Comarqueta de Incles. Se puede subir por el tablero o por las pendientes menos pronunciadas de la derecha. Cuidado con posibles lechos naturales que bajan de las rocas' WHERE id='306';
